import OverviewCard from './OverviewCard';
import DataFormatterMixin from '@/components/Mixins/DataFormatterMixin';
import { useI18n } from 'vue-i18n';
export default {
  name: 'Dumps',
  components: {
    OverviewCard
  },
  mixins: [DataFormatterMixin],
  data() {
    return {
      $t: useI18n().t
    };
  },
  computed: {
    dumps() {
      return this.$store.getters['dumps/allDumps'];
    }
  },
  created() {
    this.$store.dispatch('dumps/getAllDumps').finally(() => {
      this.$root.$emit('overview-dumps-complete');
    });
  },
  methods: {
    exportFileNameByDate() {
      // Create export file name based on date
      let date = new Date();
      date = date.toISOString().slice(0, 10) + '_' + date.toString().split(':').join('-').split(' ')[4];
      let fileName = 'all_dumps_';
      return fileName + date;
    }
  }
};
